(139, 'theme_mods_hestia', 'a:25:{i:0;b:0;s:18:"nav_menu_locations";a:2:{s:7:"primary";i:4;s:6:"footer";i:3;}s:18:"custom_css_post_id";i:-1;s:18:"hestia_page_editor";s:318:"<div class="col-md-5"><h3>About Hestia</h3>Need more details? Please check our full documentation for detailed information on how to use Hestia.</div><div class="col-md-6 col-md-offset-1"><img class="size-medium alignright" src="https://steemautomated.eu/wp-content/themes/hestia/assets/img/about-content.png" /></div>";s:24:"hestia_feature_thumbnail";s:73:"https://steemautomated.eu/wp-content/themes/hestia/assets/img/contact.jpg";s:22:"hestia_big_title_title";s:15:"Steem Automated";s:21:"hestia_big_title_text";s:29:"Reward your favourite authors";s:28:"hestia_big_title_button_text";s:7:"CONNECT";s:17:"hestia_about_hide";b:1;s:16:"hestia_blog_hide";b:1;s:19:"hestia_contact_hide";b:1;s:24:"hestia_testimonials_hide";b:1;s:16:"hestia_team_hide";b:1;s:23:"hestia_header_alignment";s:6:"center";s:26:"hestia_blog_sidebar_layout";s:10:"full-width";s:27:"hestia_enable_sharing_icons";b:0;s:21:"hestia_features_title";s:21:"What do we offer you?";s:23:"hestia_features_content";s:942:"[{"icon_value":"fa-code","color":"#e91e63","text":"Our servers run 24&#x2F;7 making sure your favourite authors get curated even when you are sleeping.","link":"#","text2":"undefined","title":"Automation","subtitle":"undefined","social_repeater":"undefined","id":"social-repeater-5b5674c2806f3","shortcode":"undefined"},{"icon_value":"fa-balance-scale","color":"#00bcd4","text":"Maintain full control over your votes with customisable rules for each author and trail.","link":"#","text2":"undefined","title":"Control","subtitle":"undefined","social_repeater":"undefined","id":"social-repeater-5b5674c251099","shortcode":"undefined"},{"icon_value":"fa-support","color":"#4caf50","text":"Never expose your private keys, use Steemconnect to maintain full control over your keys.","link":"#","text2":"undefined","title":"Security","subtitle":"undefined","social_repeater":"undefined","id":"social-repeater-5b5674c26640f","shortcode":"undefined"}]";s:27:"hestia_testimonials_content";s:1374:"[{"text":"&quot;We have no regrets! After using your product my business skyrocketed! I made back the purchase price in just 48 hours! I couldn&#039;t have asked for more than this.&quot;","link":"","text2":"undefined","image_url":"https:\\/\\/steemautomated.eu\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/5.jpg","title":"Inverness McKenzie","subtitle":"Business Owner","social_repeater":"undefined","id":"customizer_repeater_56d7ea7f40d56","shortcode":"undefined"},{"text":"&quot;Your company is truly upstanding and is behind its product 100 percent. Hestia is worth much more than I paid. I like Hestia more each day because it makes easier.&quot;","link":"","text2":"undefined","image_url":"https:\\/\\/steemautomated.eu\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/6.jpg","title":"Hanson Deck","subtitle":"Independent Artist","social_repeater":"undefined","id":"customizer_repeater_56d7ea7f40d66","shortcode":"undefined"},{"text":"&quot;Thank you for making it painless, pleasant and most of all hassle free! I am so pleased with this product. Dude, your stuff is great! I will refer everyone I know.&quot;","link":"","text2":"undefined","image_url":"https:\\/\\/steemautomated.eu\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/7.jpg","title":"Natalya Undergrowth","subtitle":"Freelancer","social_repeater":"undefined","id":"customizer_repeater_56d7ea7f40d76","shortcode":"undefined"}]";s:19:"hestia_team_content";s:4333:"[{"icon_value":"fa-facebook","text":"Locavore pinterest chambray affogato art party, forage coloring book typewriter. Bitters cold selfies, retro celiac sartorial mustache.","link":"","text2":"undefined","image_url":"https:\\/\\/steemautomated.eu\\/wp-content\\/themes\\/hestia\\/assets\\/img\\/1.jpg","title":"Desmond Purpleson","subtitle":"CEO","social_repeater":"[{&quot;id&quot;:&quot;customizer-repeater-social-repe